INSERT INTO `iqtisaditesnifat_madde` (`iqtisaditesnifat_madde_id`, `iqtisaditesnifat_paragraf_id`, `iqtisaditesnifat_madde_ad`, `iqtisaditesnifat_madde_kod`, `iqtisaditesnifat_madde_status`, `iqtisaditesnifat_madde_aciqlama`) VALUES
(5, 26, 'Tikili və avadanlıqların cari təmir xərcləri', 222110, '1', '<p><strong>222110. &ldquo;Tikili və avadanlıqların cari təmir xərcləri&rdquo;</strong>&nbsp;maddəsinə tikili və avadanlıqların texniki və iqtisadi g&ouml;stəricilərinin m&uuml;əyyən edilmiş hədd daxilində saxlanılması &uuml;&ccedil;&uuml;n (onun ayrı-ayrı tez sıradan &ccedil;ıxan (tezk&ouml;hnələn) hissələrinin və detallarının dəyişdirilməsi, bərpası, rənglənməsi, sazlanması) həyata ke&ccedil;irilən cari təmir işləri &uuml;zrə sərf olunan vəsait daxildir. Bu maddəyə, həm&ccedil;inin, tikili və avadanlıqların cari təmiri ilə bağlı mal-materialların alınması &uuml;zrə xərclər də daxildir.</p>'),
(6, 26, 'Nəqliyyat vasitələrinin cari təmir xərcləri', 222120, '1', '<p><strong>222120. &ldquo;Nəqliyyat vasitələrinin cari təmir xərcləri&rdquo;</strong>&nbsp;maddəsinə nəqliyyat vasitələrinin texniki və iqtisadi g&ouml;stəricilərinin m&uuml;əyyən edilmiş hədd daxilində saxlanılması &uuml;&ccedil;&uuml;n (onun ayrı-ayrı tez sıradan &ccedil;ıxan (tezk&ouml;hnələn) hissələrinin və detallarının dəyişdirilməsi, bərpası, rənglənməsi, sazlanması) həyata ke&ccedil;irilən təmir işləri &uuml;zrə sərf olunan məbləğlər daxildir. Bu maddəyə, həm&ccedil;inin, nəqliyyat vasitələrinin cari təmiri ilə bağlı mal-materialların alınması &uuml;zrə xərclər də daxildir.</p>'),
(7, 26, 'Sair torpaq, tikili və avadanlıqların cari təmir xərcləri', 222130, '1', '<p><strong>222130. &ldquo;Sair torpaq, tikili və avadanlıqların cari təmir xərcləri&rdquo;&nbsp;</strong>maddəsinə sair torpaq, tikili və avadanlıqların, habelə əsas fond hesab edilən digər uzunm&uuml;dətli aktivlərin texniki və iqtisadi g&ouml;stəricilərinin m&uuml;əyyən edilmiş hədd daxilində saxlanılması &uuml;&ccedil;&uuml;n (onun ayrı-ayrı tez sıradan &ccedil;ıxan (tezk&ouml;hnələn) hissələrinin və detallarının dəyişdirilməsi, bərpası, rənglənməsi, sazlanması) həyata ke&ccedil;irilən təmir işləri &uuml;zrə sərf olunan məbləğlər, habelə meliorasiya və su təsərr&uuml;fatı sistemində istismar tədbirləri &uuml;zrə g&ouml;r&uuml;lən torpaq işləri, &ccedil;aylarda tənzimlənmə, sahilbərkitmə işləri &uuml;zrə sərf olunan məbləğlər daxildir. Bu maddəyə, həm&ccedil;inin, cari təmirlə bağlı mal-materialların alınması &uuml;zrə xərclər də daxildir.</p>'),
(8, 27, 'Elektrik enerjisi haqqının ödənilməsi', 222210, '1', '<p><strong>222210. &ldquo;Elektrik enerjisi haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə istifadə olunan elektrik enerjisinin dəyərinin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(9, 27, 'Qaz haqqının ödənilməsi', 222220, '1', '<p><strong>222220. &ldquo;Qaz haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə istifadə olunan qazın və maye qazın dəyərinin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(10, 27, 'Su haqqının ödənilməsi', 222230, '1', '<p><strong>222230. &ldquo;Su haqqının &ouml;dənilməsi&rdquo; </strong>maddəsinə istifadə olunan suyun dəyərinin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(11, 27, 'Kanalizasiya xidmətləri haqqının ödənilməsi', 222240, '1', '<p><strong>222240. &ldquo;Kanalizasiya xidmətləri haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə kanalizasiya xidmətlərinin dəyərinin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(12, 27, 'İstilik enerjisi (yanacaq) haqqının ödənilməsi', 222250, '1', '<p><strong>222250. &ldquo;İstilik enerjisi (yanacaq) haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə istilik enerjisini yarada biləcək mal-materialların (odun, k&ouml;m&uuml;r, ağ neft, mazut və sair) dəyərinin &ouml;dənilməsi, elektrik və istilik enerjisi istehsal edən generator və digər avadanlıqlar &uuml;&ccedil;&uuml;n yanacaq və s&uuml;rtk&uuml; materiallarının alınması,&nbsp;habelə mərkəzləşdirilmiş istilik sistemi olan qazanxanalar tərəfindən g&ouml;stərilmiş xidmətlərin haqqının &ouml;dənilməsi ilə&nbsp;bağlı xərclər daxildir.</p>'),
(13, 27, 'Digər kommunal xidmətlərin haqqının ödənilməsi', 222290, '1', '<p><strong>222290. &ldquo;Digər kommunal xidmətlərin haqqının &ouml;dənilməsi</strong>&rdquo;&nbsp;maddəsinə&nbsp;222210-222250-ci maddələri &uuml;zrə Azərbaycan Respublikasının xaricdə olan səfirlik, konsulluq və başqa diplomatik n&uuml;mayəndəliklərinə aid olan xərclər, elektrik, rabitə, qaz, su və digər kommunal xətlərinin &ccedil;əkilişi, tullantıların ləğvi və daşınması, t&uuml;st&uuml; bacalarının təmizlənməsi, &ccedil;amaşırların yuyulması, h&uuml;nd&uuml;rmərtəbəli inzibati və digər təyinatlı binalarda ş&uuml;şələrin yuyulması, kanalizasiya xətləri olmayan yerlərdə &ccedil;irkab suların daşınması, kommunal xidmətlərdə istifadə olunan smart kartların haqqının &ouml;dənilməsi və bu qəbildən olan digər xərclər daxildir.</p>'),
(14, 28, 'Telefon danışıq haqlarının ödənilməsi', 222310, '1', '<p>222310. &ldquo;Telefon danışıq haqlarının &ouml;dənilməsi&rdquo;&nbsp;maddəsinə &ldquo;222311. &Ouml;lkədaxili telefon danışıq haqlarının &ouml;dənilməsi&rdquo; və &ldquo;222312. Beynəlxalq telefon danışıqları haqlarının &ouml;dənilməsi&rdquo; yarımmaddələrinin xərcləri daxildir.</p>'),
(15, 28, 'Poçt xidmətləri haqqının ödənilməsi', 222320, '1', '<p><strong>222320. &ldquo;Po&ccedil;t xidmətləri haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə məktub və b&uuml;t&uuml;n n&ouml;v po&ccedil;t, teleqramların g&ouml;ndərilməsi, feldrabitə, radioqram, teletayp, pul (baratlar) g&ouml;ndərişləri, &ccedil;ap materiallarının &ccedil;atdırılması ilə bağlı xərclər, həm&ccedil;inin 222311-ci və 222312-ci yarımmaddələri &uuml;zrə Azərbaycan Respublikasının xaricdə olan səfirlik, konsulluq və başqa diplomatik n&uuml;mayəndəliklərinə aid olan xərclər daxildir.</p>'),
(16, 28, 'İnternet xidməti haqqının ödənilməsi', 222330, '1', '<p><strong>222330. &ldquo;İnternet xidməti haqqının &ouml;dənilməsi&rdquo;</strong>&nbsp;maddəsinə internet xidmətindən istifadəyə g&ouml;rə &ouml;dənişlər, vebsaytların yeniləndirilməsi və saxlanılması, internet şəbəkəsinin domen adları serverində qeydiyyata alınması və bu qəbildən olan digər xərclər daxildir.</p>'),
(17, 28, 'Proqram təminatlarına xidmət haqlarının ödənilməsi', 222340, '1', '<p>222340. &ldquo;Proqram təminatlarına xidmət haqlarının &ouml;dənilməsi&rdquo;&nbsp;maddəsinə proqram təminatına xidmətlə bağlı xərclər,&nbsp;informasiya təhl&uuml;kəsizliyinin təmin edilməsi xərcləri&nbsp;və bu qəbildən olan digər xərclər daxildir.</p>'),
(18, 29, 'İcarə və muzdlu xidmətlər', 222410, '1', '<p><strong>222410. &ldquo;İcarə və muzdlu xidmətlər&rdquo;</strong>&nbsp;maddəsinə cari məqsədlər &uuml;&ccedil;&uuml;n binaların və evlərin icarəsi, film və verilişlərin &ccedil;əkilməsi &uuml;&ccedil;&uuml;n icarəyə g&ouml;t&uuml;r&uuml;lm&uuml;ş teatr, konsert salonları, sahə və otaqların icarəsi haqqı, icarəyə g&ouml;t&uuml;r&uuml;lən sahə ilə bağlı depozit və makler xərci, m&uuml;əssisə və təşkilatlar tərəfindən avadanlıq və nəqliyyat vasitələrinin icarəyə g&ouml;t&uuml;r&uuml;lməsi ilə bağlı xərclər daxildir.</p>'),
(20, 29, 'Kirayə haqqı', 222420, '1', '<p><strong>222420. &ldquo;Kirayə haqqı&rdquo;</strong>&nbsp;maddəsinə pulsuz verilən mənzillərə g&ouml;rə kirayə haqqının &ouml;dənilməsi xərcləri daxildir.</p>'),
(21, 29, 'Mühafizə xərcləri', 222430, '1', '<p><strong>222430. &ldquo;M&uuml;hafizə xərcləri&rdquo;</strong>&nbsp;maddəsinə təşkilatın balansında olan və ya icarəyə g&ouml;t&uuml;r&uuml;lm&uuml;ş obyektlərin m&uuml;hafizəsi ilə bağlı xərclər daxildir.</p>'),
(22, 29, 'Əsas fondların və digər aktivlərin saxlanılması xərcləri', 222440, '1', '<p><strong>222440. &ldquo;Əsas fondların və digər aktivlərin saxlanılması xərcləri&rdquo;</strong>&nbsp;maddəsinə hesablama texnikası və avadanlıqlara g&ouml;stərilən xidmət haqları, liftlərə texniki xidmətlə bağlı xərclər, soyutma və isitmə sistemlərinə, inzibati binalara buraxılış sistemlərinə (turniketlərə) xidmət haqlarının &ouml;dənilməsi, dezinfeksiya xərcləri, bina və tikililərin fasadlarının yuyulması və təmizlənməsi ilə bağlı və bu qəbildən olan digər xərclər daxildir. Bu maddə &ldquo;222441. Əsas fondlara xidmət haqqı xərcləri&rdquo; və &ldquo;222442. Sanitariya-gigiyena və digər xidmət haqqı xərcləri&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(23, 30, 'Ölkədaxili ezamiyyətlər', 222510, '1', '<p><strong>222510. &ldquo;&Ouml;lkədaxili ezamiyyətlər&rdquo;</strong>&nbsp;maddəsinə &ouml;lkə daxilindəki ezamiyyətlərlə bağlı xərclər daxildir.</p>'),
(24, 30, 'Xarici ezamiyyətlər', 222520, '1', '<p><strong>222520. &ldquo;Xarici ezamiyyətlər&rdquo;&nbsp;</strong>maddəsinə xarici &ouml;lkələrə ezamiyyətlər və xidməti səfərlərlə bağlı &ouml;dənişlər, həm&ccedil;inin 222510 maddəsi &uuml;zrə Azərbaycan Respublikasının xaricdə olan səfirlik, konsulluq və başqa diplomatik n&uuml;mayəndəliklərinə aid olan xərclər daxildir.</p>'),
(25, 34, 'Atributların, mükafatlandırma nişanlarının hazırlanması, təltif və mükafatlandırma ilə bağlı xərclər', 222910, '1', '<p><strong>222910. &ldquo;Atributların, m&uuml;kafatlandırma nişanlarının hazırlanması, təltif və m&uuml;kafatlandırma ilə bağlı xərclər&rdquo;</strong>&nbsp;maddəsinə d&ouml;vlət atributlarının, m&uuml;xtəlif m&uuml;kafatlandırma nişanlarının hazırlanması və alınması, b&uuml;t&uuml;n n&ouml;v təltiflər &uuml;zrə xərclər, kəşf, ixtira və səmərələşdirici təkliflərlə bağlı xərclərin &ouml;dənilməsi, fəxri fərmanların, medalların, ordenlərin, eləcə də m&uuml;xtəlif yarışların və tədbirlərin iştirak&ccedil;ılarına m&uuml;kafatların, suvenirlərin və hədiyyələrin alınması, digər dəyərləndirmə tədbirləri ilə bağlı xərclər və bu qəbildən olan digər xərclər daxildir.</p>'),
(26, 34, 'İstehlakyönlü müxtəlif xidmətlər', 222920, '1', '<p>222920. &ldquo;İstehlaky&ouml;nl&uuml; m&uuml;xtəlif xidmətlər&rdquo;&nbsp;maddəsinə musiqi və bədii&nbsp;əsərlərin alınması (sifarişi), xarici &ouml;lkələrlə mədəni əlaqələr, elmi sessiyaların, qurultayların, konfransların, seminarların, iclasların, bayram tədbirlərinin təşkili, ke&ccedil;irilməsi və iştirak xərcləri,&nbsp;&ouml;lkə səviyyəli&nbsp;sərgi və&nbsp;yarmarkaların ke&ccedil;irilməsi, o c&uuml;mlədən ədəbiyyat, incəsənət və digər sahələrlə bağlı m&uuml;vəqqəti sərgi və yarmarkaların, ədəbi musiqi m&uuml;sabiqələrinin təşkili, ke&ccedil;irilməsi, ekskursiyaların və d&uuml;şərgə toplantılarının ke&ccedil;irilməsi, &uuml;mumrespublika gənclər, idman və turizm tədbirlərinin, təlim-məşq yarış və toplantıların təşkili, ke&ccedil;irilməsi və iştirak xərcləri, həm&ccedil;inin bu maddə &uuml;zrə qeyd olunan b&uuml;t&uuml;n n&ouml;v toplantı, yarış və tədbirlərə dəvətlilərin gediş, g&uuml;ndəlik, mehmanxana və mənzil xərcləri, sanitar aviasiyasının u&ccedil;uş saatlarının &ouml;dənilməsi, tədris m&uuml;əssisələrinin nəzdindəki eksperimental emalatxanaların xidmətinin &ouml;dənilməsi, xidməti iş heyvanlarının saxlanılması, yanğıns&ouml;nd&uuml;rən balonların, katriclərin, tibbi və digər avadanlıqların doldurulması, mətbuatda elan, bildiriş, reklam verilməsi, &ouml;l&ccedil;&uuml; cihazlarının dəqiqliyinin &ouml;l&ccedil;&uuml;lməsi &uuml;zrə xidmət haqlarının &ouml;dənilməsi, təhsil m&uuml;əssisələrində bədən tərbiyəsi tədbirlərinin və k&uuml;tləvi informasiya işlərinin aparılması, arxiv materiallarından istifadə edilməsi və arxivə verilmiş sənədlərə xidmət haqqının &ouml;dənilməsi, sənədlərin cildlənməsi, tikilməsi, informasiya agentliklərinin xidmətləri, guşələrin yaradılması və bədii tərtibat işlərinin dəyərinin &ouml;dənilməsi, televiziya və radio verilişləri &uuml;zrə (x&uuml;susi kino &ccedil;əkilişi, texniki rabitə vasitələrinin istismarı, xidmətlərin &ouml;dənilməsi), muzey ekspozisiyalarının və sərgilərin təşkili zamanı m&uuml;xtəlif m&uuml;vəqqəti xarakter daşıyan və k&ouml;mək&ccedil;i materiallar olan xəritələrin, sxemlərin, diaqramların, eskizlərin, maketlərin və digər əşyaların hazırlanması, reklam xarakterli təbliğat-təşviqat xarakterli video və audio materialların hazırlanması və yayılması xərcləri, əmlakın sığortalanması ilə bağlı xərclər, d&ouml;vlət hesabına h&uuml;quqi yardımın &ouml;dənilməsi, məhkəmə prosesində iştirak edən, habelə icraatında inzibati xəta haqqında iş olan hakimin yanına, səlahiyyətli orqana (vəzifəli şəxsin yanına) gəlməklə əlaqədar zərər &ccedil;əkmiş, tərc&uuml;mə&ccedil;i, ekspert, m&uuml;təxəssis, şahid və hal şahidlərinə g&ouml;r&uuml;lən işin haqqının &ouml;dənilməsi, ekspertlərin və tərc&uuml;mə&ccedil;ilərin xidmətlərinin &ouml;dənilməsi, əhalini k&ouml;&ccedil;&uuml;rmə tədbirləri, Azərbaycan Respublikasının xaricdə olan səfirlik, konsulluq və başqa diplomatik n&uuml;mayəndəliklərinin əməkdaşlarının və onların ailə &uuml;zvlərinin m&uuml;əyyən edilmiş norma daxilində y&uuml;klərinin daşınması, həm&ccedil;inin 222910-cu maddəsində qeyd olunmayan digər xidmətlərlə bağlı xərclər daxildir. 222910-cu maddəsi &uuml;zrə Azərbaycan Respublikasının xaricdə olan səfirlik, konsulluq və başqa diplomatik n&uuml;mayəndəliklərinə aid olan xərclər daxildir.</p>'),
(27, 34, 'Abadlaşdırma xidmətləri haqqının ödənilməsi', 222930, '1', '<p><strong>222930. &ldquo;Abadlaşdırma xidmətləri haqqının &ouml;dənilməsi&rdquo;&nbsp;</strong>maddəsinin xərclərinə şəhər, rayon mərkəzlərinin, habelə şəhər k&uuml;&ccedil;ələrinin və qəsəbələrin, həyətyanı sahələrin və başqa yaşayış məntəqələrinin yaşıllaşdırılması, işıqlandırılması, k&uuml;&ccedil;ə və meydanların s&uuml;p&uuml;r&uuml;lməsi, yuyulub təmizlənməsi, bayram tədbirləri ilə bağlı plakatların və ş&uuml;arların hazırlanması, sahibsiz heyvanlarla bağlı xərclər, k&uuml;&ccedil;ə adlarının yazılması, fəvvarələrin saxlanılması &uuml;&ccedil;&uuml;n mal və materialların alınması ilə bağlı xərclər, heykəllərin yuyulması, istismarı ilə bağlı xərclər (bu vəsaitlərin planlaşdırılması və xərclənməsi yalnız ixtisaslaşdırılmış quraşdırma-istismar b&ouml;lmələri olmadıqda aparılır), həm&ccedil;inin yol-k&ouml;rp&uuml; təsərr&uuml;fatları &uuml;&ccedil;&uuml;n mal və materialların alınması ilə bağlı xərclər daxildir.</p>'),
(28, 34, 'Bank və plastik kartların xərcləri', 222940, '1', '<p><strong>222940. &ldquo;Bank və plastik kartların xərcləri&rdquo;</strong>&nbsp;maddəsinə m&uuml;əssisə və təşkilatlara banklar tərəfindən g&ouml;stərilən bank xidmətləri, plastik kartlarının haqqının &ouml;dənilməsi və bu qəbildən olan digər xərclər daxildir. Bu paraqraf 2 yarımmaddədən, o c&uuml;mlədən &ldquo;222941. Bank xidmətləri haqqının &ouml;dənilməsi&rdquo; və &ldquo;222942. Plastik kartların dəyəri və digər &ouml;dənişlər&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(29, 34, 'Əməyin mühafizəsi xərcləri', 222950, '1', '<p><strong>222950. &ldquo;Əməyin m&uuml;hafizəsi xərcləri&rdquo;</strong>&nbsp;maddəsinə Azərbaycan Respublikasının Əmək Məcəlləsinin 220-ci maddəsinin tələblərinin icrasını təmin etmək məqsədi ilə əməyin m&uuml;hafizəsi &uuml;zrə elmi tədqiqat işlərinin maliyyələşdirilməsi, məqsədli d&ouml;vlət və regional proqramlarının hazırlanması və yerinə yetirilməsi &uuml;&ccedil;&uuml;n sərf olunan xərclər aiddir.</p>'),
(30, 34, 'Sair xərclər', 222990, '1', '<p><strong>222990.&nbsp;&ldquo;Sair xərclər&rdquo;</strong>&nbsp;maddəsinə vizasız və pasportsuz yaşayan əcnəbilərin respublikanın h&uuml;dudlarından &ccedil;ıxarılması (deportasiyası), b&uuml;dcə təşkilatlarının ixtisasartırma m&uuml;əssisələrində dinləyicilərin ixtisasının artırılması işlərinin təşkili ilə bağlı xərclər, şagirdlərin və tələbələrin (ali və orta ixtisas təhsil m&uuml;əssisələrinə) yeni qəbulunun təşkili, şagirdlərin istehsal təcr&uuml;bəsi ke&ccedil;irilməsinə g&ouml;rə vəsaitin &ouml;dənilməsi, şagirdlərin, tələbələrin yol haqqının və onların yolda və təcr&uuml;bə yerlərinə gətirilməsi və saxlanılması xərcləri, statistik tədqiqatların aparılması, xarici ezamiyyələr &uuml;&ccedil;&uuml;n viza, m&uuml;xtəlif r&uuml;sum və &ouml;dənclərlə bağlı xərclər, nəqliyyatın b&uuml;t&uuml;n n&ouml;vlərindən (taksidən başqa) istifadə edilməsi ilə əlaqədar məbləğ, həlak olanların cəsədinin gətirilməsi, dəfn və dəfn mərasimi xərclərinin &ouml;dənilməsi ilə bağlı xərclər, məhbusların daşınması, həqiqi hərbi xidmətdən buraxılan hərbi qulluq&ccedil;uların yaşadıqları yerə getməsi, hərbi qulluq&ccedil;uların (x&uuml;susi r&uuml;tbəsi olan əməkdaşların) və onların ailə &uuml;zvlərinin yeni xidmət yerinə k&ouml;&ccedil;məsi, əmlakın daşınması və birdəfəlik kompensasiyaların &ouml;dənilməsi, sanatoriya, istirahət evlərinə getməsi, m&uuml;əyyən olunmuş qaydada əməliyyat-axtarış, kəşfiyyat və əks-kəşfiyyat fəaliyyəti ilə bağlı xərclər, məhkəmənin qərarlarına əsasən fiziki və h&uuml;quqi şəxslərə dəymiş maddi və mənəvi ziyanın &ouml;dənilməsi, aztəminatlı şəxslərə, cinayət işləri &uuml;zrə m&uuml;ttəhimlərə və inzibati qaydada tutulmuş şəxslərə h&uuml;quqi yardımın g&ouml;stərilməsi,&nbsp;&ouml;lm&uuml;ş zərər &ccedil;əkmiş şəxsin h&uuml;quqi varisi qismində tanınmış vəkil tərəfindən g&ouml;stərilən h&uuml;quqi yardımın &ouml;dənilməsi ilə bağlı xərclər,&nbsp;habelə maddi imkanı olmayan vətəndaşlara d&ouml;vlət tərəfindən g&ouml;stərilən h&uuml;quqi yardımla bağlı xərclər, məcburi işburaxma hallarının və əmək m&uuml;bahisələri &uuml;zrə digər &ouml;dənişlərin verilməsi, tədqiqat, ibtidai istintaq, prokurorluq və məhkəmə orqanlarının qanunsuz hərəkətləri nəticəsində fiziki şəxslərə vurulmuş ziyanın &ouml;dənilməsi, məhkəmələrə verilmiş iddia ərizələri və notariat əməliyyatları &uuml;zrə d&ouml;vlət r&uuml;sumunun &ouml;dənilməsi (b&uuml;dcə təşkilatları, Mərkəzi Bank və onun qurumları istisna olmaqla) xərcləri, podrat, tapşırıq, komisyon, m&uuml;əlliflik və başqa m&uuml;lki-h&uuml;quqi m&uuml;qavilələr &uuml;zrə işləri yerinə yetirən şəxslərə &ouml;dənilən məbləğlər, həm&ccedil;inin d&ouml;vlət b&uuml;dcəsində nəzərdə tutulan ehtiyat fondlarının, sair əməliyyatların və məsrəflərin &ouml;dənilməsi xərcləri daxildir.[12]</p>'),
(31, 35, 'Beynəlxalq səviyyəli tədbirlərin keçirilməsi', 224110, '1', '<p><strong>224110. &ldquo;Beynəlxalq səviyyəli tədbirlərin ke&ccedil;irilməsi&rdquo;</strong>&nbsp;maddəsinə &ouml;lkə daxilində və xaricdə beynəlxalq səviyyəli tədbirlərin (forum, konfrans, seminar, simpozium, sərgi, yarmarka və s.) təşkili ilə bağlı xərclər daxildir.</p>'),
(32, 35, 'Xarici ölkələrin nümayəndə heyətlərinin və rəsmi şəxslərinin qəbulu, onlara xidmət göstərilməsi', 224120, '1', '<p><strong>224120. &ldquo;Xarici &ouml;lkələrin n&uuml;mayəndə heyətlərinin və rəsmi şəxslərinin qəbulu, onlara xidmət g&ouml;stərilməsi&rdquo;</strong>&nbsp;maddəsinə xarici &ouml;lkələrin n&uuml;mayəndə heyətlərinin və rəsmi şəxslərinin qəbulu, onlara xidmət g&ouml;stərilməsi ilə bağlı xərclər daxildir.</p>'),
(33, 36, 'İxracın və xarici investisiyaların təşviqi', 224210, '1', '<p><strong>224210. &ldquo;İxracın və xarici investisiyaların təşviqi&rdquo;</strong>&nbsp;maddəsinə ixracın və investisiyaların təşviqi ilə bağlı xərclər daxildir.</p>'),
(34, 36, 'Azərbaycan Respublikasının xaricdəki diplomatik nümayəndəlikləri və konsulluqlarının təşviqat fəaliyyəti üzrə xərclər', 224220, '1', '<p><strong>224220. &ldquo;Azərbaycan Respublikasının xaricdəki diplomatik n&uuml;mayəndəlikləri və konsulluqlarının təşviqat fəaliyyəti &uuml;zrə xərclər&rdquo;</strong>&nbsp;maddəsinə Azərbaycan Respublikasının xaricdəki diplomatik n&uuml;mayəndəlikləri və konsulluqlarının təşviqat fəaliyyəti ilə bağlı digər xərclər daxildir.</p>'),
(35, 49, 'Naxçıvan Muxtar Respublikasının büdcəsinə verilən dotasiya', 261310, '1', '<p><strong>261310. &ldquo;Nax&ccedil;ıvan Muxtar Respublikasının b&uuml;dcəsinə verilən dotasiya&rdquo;</strong>&nbsp;maddəsinə d&ouml;vlət b&uuml;dcəsindən Nax&ccedil;ıvan Muxtar Respublikasının b&uuml;dcəsinə onların gəlir və xərclərini tənzimləmək məqsədi ilə əvəzsiz verilən maliyyə vəsaiti daxildir.</p>'),
(36, 49, 'Yerli büdcələrə verilən dotasiya', 261320, '1', '<p><strong>261320. &ldquo;Yerli b&uuml;dcələrə verilən dotasiya&rdquo;</strong>&nbsp;maddəsinə d&ouml;vlət b&uuml;dcəsindən yerli b&uuml;dcələrə onların gəlir və xərclərini tənzimləmək məqsədi ilə əvəzsiz verilən maliyyə vəsaiti daxildir.</p>'),
(37, 50, 'Naxçıvan Muxtar Respublikasının büdcəsinə verilən subvensiya', 261410, '1', '<p><strong>261410. &ldquo;Nax&ccedil;ıvan Muxtar Respublikasının b&uuml;dcəsinə verilən subvensiya&rdquo;</strong>&nbsp;maddəsinə d&ouml;vlət b&uuml;dcəsindən Nax&ccedil;ıvan Muxtar Respublikasının b&uuml;dcəsinə verilən, lakin həmin məqsəd &uuml;&ccedil;&uuml;n və ya m&uuml;əyyən edilmiş m&uuml;ddətdə istifadə edilmədikdə geri qaytarılan maliyyə vəsaiti daxildir.</p>'),
(38, 50, 'Yerli büdcələrə verilən subvensiya', 261420, '1', '<p><strong>261420. &ldquo;Yerli b&uuml;dcələrə verilən subvensiya&rdquo;</strong>&nbsp;maddəsinə d&ouml;vlət b&uuml;dcəsindən yerli b&uuml;dcələrə verilən, lakin həmin məqsəd &uuml;&ccedil;&uuml;n və ya m&uuml;əyyən edilmiş m&uuml;ddətdə istifadə edilmədikdə geri qaytarılan maliyyə vəsaiti daxildir.</p>'),
(39, 54, 'Yaşa görə əmək pensiyası', 271110, '1', '<p><strong>271110. &ldquo;Yaşa g&ouml;rə əmək pensiyası&rdquo;&nbsp;</strong>maddəsinə qanunvericiliyə uyğun olaraq yaşa g&ouml;rə verilən əmək pensiyası ilə bağlı xərclər daxildir. Bu maddə &ldquo;271111. Yaşa g&ouml;rə əmək pensiyasının sığorta hissəsi&rdquo; və &ldquo;271112. Yaşa g&ouml;rə əmək pensiyasının yığım hissəsi&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(40, 54, 'Əlilliyə görə əmək pensiyası', 271120, '1', '<p><strong>271120. &ldquo;Əlilliyə g&ouml;rə əmək pensiyası&rdquo;</strong>&nbsp;maddəsinə m&ouml;vcud qanunvericiliyə uyğun olaraq əlilliyə g&ouml;rə verilən əmək pensiyası ilə bağlı xərclər daxildir. Bu maddə &ldquo;271121. Əlilliyə g&ouml;rə əmək pensiyasının sığorta hissəsi&rdquo; və &ldquo;271122. Əlilliyə g&ouml;rə əmək pensiyasının yığım hissəsi&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(41, 54, 'Ailə başçısını itirməyə görə əmək pensiyası', 271130, '1', '<p><strong>271130. &ldquo;Ailə baş&ccedil;ısını itirməyə g&ouml;rə əmək pensiyası&rdquo;&nbsp;</strong>maddəsinə m&ouml;vcud qanunvericiliyə uyğun olaraq ailə baş&ccedil;ısını itirməyə g&ouml;rə əmək pensiyasının sığorta hissəsinə aid edilən xərclər daxildir.</p>'),
(42, 55, '3 yaşınadək uşağa qulluğa görə müavinət', 271210, '1', '<p><strong>271210. &ldquo;3 yaşınadək uşağa qulluğa g&ouml;rə m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə 3 yaşına &ccedil;atmamış uşaqlara qulluğa g&ouml;rə &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(43, 55, 'Əmək qabiliyyətini müvəqqəti itirməyə görə müavinət', 271220, '1', '<p><strong>271220. &ldquo;Əmək qabiliyyətini m&uuml;vəqqəti itirməyə g&ouml;rə m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə m&ouml;vcud qanunvericilikdə nəzərdə tutulmuş m&uuml;vəqqəti əmək qabiliyyətini itirməyə g&ouml;rə iş&ccedil;ilərə &ouml;dənilən m&uuml;avinətlər, o c&uuml;mlədən əmək qabiliyyətini m&uuml;vəqqəti itirmiş iş&ccedil;ilərə ilk 14 g&uuml;n &uuml;&ccedil;&uuml;n işəg&ouml;t&uuml;rənin vəsaiti hesabına &ouml;dənilən m&uuml;avinətlər &uuml;zrə xərclər daxildir.</p>'),
(44, 55, 'Uşağın doğulması ilə bağlı müavinət', 271230, '1', '<p><strong>271230. &ldquo;Uşağın doğulması ilə bağlı m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə uşaq doğularkən ata və ya ananın iş yerindən, işləməyənlər &uuml;&ccedil;&uuml;n isə sosial m&uuml;dafiə mərkəzlərindən birdəfəlik &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(45, 55, 'Hamiləliyə və doğuşa görə müavinət', 271240, '1', '<p><strong>271240. &ldquo;Hamiləliyə və doğuşa g&ouml;rə m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə analıq məzuniyyətinə &ccedil;ıxan qadınlara &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(46, 55, 'Əmək zədəsi və ya peşə xəstəliyi üzrə ödənişlər', 271250, '1', '<p><strong>271250. &ldquo;Əmək zədəsi və ya peşə xəstəliyi &uuml;zrə &ouml;dənişlər&rdquo;&nbsp;</strong>maddəsinə istehsalatda baş vermiş bədbəxt hadisə və ya peşə xəstəliyi nəticəsində sağlamlığı pozulmuş iş&ccedil;ilərə, habelə bu səbəbdən həlak olmuş iş&ccedil;ilərin ailə &uuml;zvlərinə və himayəsində olan digər şəxslərə qanunvericiliklə m&uuml;əyyən edilmiş qaydada dəymiş zərərə g&ouml;rə &ouml;dənişlərlə bağlı xərclər daxildir.</p>'),
(47, 55, 'Dəfn üçün müavinət', 271260, '1', '<p><strong>271260. &ldquo;Dəfn &uuml;&ccedil;&uuml;n m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə m&ouml;vcud qanunvericiliklə m&uuml;əyyən olunmuş hallarda məcburi d&ouml;vlət sosial sığorta haqqı və d&ouml;vlət b&uuml;dcəsinin vəsaiti hesabına iş&ccedil;inin vəfatı ilə əlaqədar əmək m&uuml;qaviləsinə xitam verildikdə, vəfat edənin vərəsələrinə ortaaylıq əməkhaqqının qanunvericiliklə m&uuml;əyyən olunmuş məbləğində m&uuml;avinətin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(48, 55, 'Digər müavinətlər', 271290, '1', '<p><strong>271290. &ldquo;Digər m&uuml;avinətlər&rdquo;&nbsp;</strong>maddəsinə 271210-271260-cı maddələrində nəzərdə tutulmayan&nbsp;və mahiyyət etibarilə m&uuml;avinət hesab edilən digər &ouml;dənişlər &uuml;zrə xərclər daxildir.</p>'),
(49, 56, 'Aktiv məşğulluqtədbirləri üzrə ödənişlər[6]', 271910, '1', '<p><strong>271910. &ldquo;Aktiv məşğulluq&nbsp;tədbirləri &uuml;zrə &ouml;dənişlər&rdquo;</strong>&nbsp;maddəsinə&nbsp;aktiv&nbsp;məşğulluq tədbirləri ilə bağlı xərclər daxildir. Bu maddə &ldquo;271911.&nbsp;İşsizlikdən&nbsp;sığorta &ouml;dənişi&rdquo;,&nbsp;&ldquo;271912. Peşə&nbsp;hazırlığının&nbsp;təşkili&rdquo;, &ldquo;271913.&nbsp;Haqqı &ouml;dənilən&nbsp;ictimai işlərin təşkili&rdquo;, &ldquo;271914.&nbsp;Peşəy&ouml;n&uuml;m&uuml;nə dair məsləhət xidmətlərinin g&ouml;stərilməsi&rdquo;, &ldquo;271915.&nbsp;&Ouml;z&uuml;nəməşğulluq tədbirlərinin təşkili&rdquo;, &ldquo;271916.&nbsp;Əmək yarmarkalarının və əmək birjalarının təşkili&rdquo;,&nbsp;&ldquo;271917.&nbsp;İşəg&ouml;t&uuml;rənlərlə birlikdə iş&ccedil;ilərin&nbsp;əməkhaqqının maliyyələşdirilməsi&rdquo;, &ldquo;271918. &ldquo;Sosial m&uuml;dafiəyə x&uuml;susi ehtiyacı olan və işə d&uuml;zəlməkdə &ccedil;ətinlik &ccedil;əkən&nbsp;şəxslərin&nbsp;məşğulluğunun təmin olunması&rdquo;&nbsp;və &ldquo;271919.&nbsp;Aktiv məşğulluqtədbirləri &uuml;zrə digər &ouml;dənişlər&rdquo; yarımmaddələrdən ibarətdir.</p>'),
(50, 56, 'Əlilliklə bağlı ödənişlər', 271920, '1', '<p><strong>271920. &ldquo;Əlilliklə bağlı &ouml;dənişlər&rdquo;&nbsp;</strong>maddəsinə əlillərin sosial m&uuml;dafiəsi ilə bağlı xərclər daxildir. Bu maddə &ldquo;271921. Əlillərin sağlamlığının m&uuml;hafizəsi və tibbi bərpası&rdquo;, &ldquo;271922. Reabilitasiya tədbirlərinin təşkili&rdquo;, &ldquo;271923. Əlillərə maddi və texniki yardımın təşkili&rdquo; və &ldquo;271929. Digər &ouml;dənişlər&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(51, 58, 'Şəhid olanların uşaqlarına müavinətlər', 272210, '1', '<p><strong>272210. &ldquo;Şəhid olanların uşaqlarına m&uuml;avinətlər&rdquo;</strong>&nbsp;maddəsinə şəhid olanların uşaqlarına &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(52, 58, 'Müharibə və 20 yanvar hadisəsi zamanı əlil olan ailələrin uşaqlarına aylıq müavinət', 272220, '1', '<p><strong>272220. &ldquo;M&uuml;haribə və 20 yanvar hadisəsi zamanı əlil olan ailələrin uşaqlarına aylıq m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə m&uuml;haribə əlilləri və 20 yanvar hadisəsi zamanı əlil olan ailələrin uşaqlarına &ouml;dənilən aylıq m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(53, 58, 'Çernobıl qəzasının nəticələrinin aradan qaldırılması ilə bağlı müavinət', 272230, '1', '<p><strong>272230. &ldquo;&Ccedil;ernobıl qəzasının nəticələrinin aradan qaldırılması ilə bağlı m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə &Ccedil;ernobıl qəzası nəticəsində I və II qrup əlil olmuş, yaxud vəfat etmiş valideynlərin, habelə &Ccedil;ernobıl qəzasının nəticələrinin aradan qaldırılmasında iştirak etmiş dispanser qeydiyyatında olanların uşaqlarına &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(54, 58, 'Müddətli həqiqi hərbi xidmətdə olan ailələrin uşaqlarına müavinət', 272240, '1', '<p><strong>272240. &ldquo;M&uuml;ddətli həqiqi hərbi xidmətdə olan ailələrin uşaqlarına m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə m&uuml;ddətli hərbi xidmətdə olan ailələrin uşaqlarına &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(55, 58, 'Bir yaşadək uşağı olan aztəminatlı ailələrə müavinət', 272250, '1', '<p><strong>272250. &ldquo;Bir yaşadək uşağı olan aztəminatlı ailələrə m&uuml;avinət&rdquo;&nbsp;</strong>maddəsinə &uuml;nvanlı d&ouml;vlət sosial yardımı almaq h&uuml;ququna malik olan ailələrdə olan bir yaşadək uşağa g&ouml;rə &ouml;dənilən m&uuml;avinətlərlə bağlı xərclər daxildir.</p>'),
(56, 58, 'Digər müavinətlər', 272290, '1', '<p><strong>272290. &ldquo;Digər m&uuml;avinətlər&rdquo;</strong>&nbsp;maddəsinə 272210-272250-ci maddələrdə nəzərdə tutulmayan&nbsp;və mahiyyət etibarilə aztəminatlı ailələrə verilən uşaq m&uuml;avinəti hesab edilən digər xərclər daxildir.</p>'),
(57, 59, 'Yaşa görə müavinət', 272310, '1', '<p><strong>272310. &ldquo;Yaşa g&ouml;rə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə əmək qabiliyyəti olmayanlara əmək pensiyası h&uuml;ququ olmadıqda və işləmədikdə &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(58, 59, 'Əlilliyə görə müavinət', 272320, '1', '<p><strong>272320. &ldquo;Əlilliyə g&ouml;rə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə əlillərə əmək pensiyası h&uuml;ququ olmadıqda, &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(59, 59, 'Sağlamlıq imkanları məhdud 16 yaşadək əlil uşaqlara müavinət', 272330, '1', '<p><strong>272330. &ldquo;Sağlamlıq imkanları məhdud 16 yaşadək əlil uşaqlara m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə 16 yaşadək əlil uşaqlara &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(60, 59, 'Ailə başçısını itirməyə görə müavinət', 272340, '1', '<p><strong>272340. &ldquo;Ailə baş&ccedil;ısını itirməyə g&ouml;rə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə ailə baş&ccedil;ısını itirməyə g&ouml;rə &ouml;dənilən sosial m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(61, 59, 'Qəyyumluğa görə müavinət', 272350, '1', '<p><strong>272350. &ldquo;Qəyyumluğa g&ouml;rə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə valideynlərini itirmiş və valideyn himayəsindən məhrum olmuş uşaqlara qanunvericiliklə qəyyumluq (himayə&ccedil;ilik) m&uuml;əyyən edildikdə, &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(62, 59, 'Müalicə xərci', 272360, '1', '<p><strong>272360. &ldquo;M&uuml;alicə xərci&rdquo;</strong>&nbsp;maddəsinə radiasiya qəzasının ləğvində iştirak etmiş və həmin qəza nəticəsində zərər &ccedil;əkərək əlil olmuş şəxslərə hər il &uuml;&ccedil;&uuml;n m&uuml;alicə xərclərinə g&ouml;rə &ouml;dənilən birdəfəlik m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(63, 59, 'Digər müavinətlər', 272390, '1', '<p><strong>272390. &ldquo;Digər m&uuml;avinətlər&rdquo;</strong>&nbsp;maddəsinə 272310-272360-cı maddələrdə nəzərdə tutulmayan və mahiyyət etibarilə d&ouml;vlət sosial yardımı hesab edilən digər&nbsp;m&uuml;avinətlərin &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(64, 60, 'Şəhid ailəsi üzvlərinə müavinət', 272410, '1', '<p><strong>272410. &ldquo;Şəhid ailəsi &uuml;zvlərinə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə şəhid ailəsi &uuml;zvlərinin hər birinə qanunvericiliklə m&uuml;əyyən edilmiş qaydada kommunal, nəqliyyat və digər xidmətlərə g&ouml;rə g&uuml;zəştlərin əvəzinə verilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(65, 60, 'Müharibə veteranlarına müavinət', 272420, '1', '<p><strong>272420. &ldquo;M&uuml;haribə veteranlarına m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə m&uuml;haribə veteranı statusu qazanmış şəxslərə kommunal, nəqliyyat və digər xidmətlərə g&ouml;rə g&uuml;zəştlərin əvəzinə &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(66, 60, 'Digər müavinətlər', 272490, '1', '<p><strong>272490. &ldquo;Digər m&uuml;avinətlər&rdquo;</strong>&nbsp;maddəsinə 272410-272420-ci maddələrdə kommunal g&uuml;zəştlər &uuml;zrə nəzərdə tutulmayan və mahiyyət etibarilə kommunal g&uuml;zəşt hesab olunan digər xərclər daxildir.</p>'),
(67, 61, 'Fəxri adlara görə təqaüd', 272510, '1', '<p><strong>272510. &ldquo;Fəxri adlara g&ouml;rə təqa&uuml;d&rdquo;</strong>&nbsp;maddəsinə &ldquo;xalq&rdquo; və &ldquo;əməkdar&rdquo; fəxri adlarına g&ouml;rə &ouml;dənilən təqa&uuml;dlə bağlı xərclər daxildir.</p>'),
(68, 61, 'Milli Qəhrəman adını alanlara verilən təqaüd', 272520, '1', '<p><strong>272520. &ldquo;Milli Qəhrəman adını alanlara verilən təqa&uuml;d&rdquo;</strong>&nbsp;maddəsinə Azərbaycanın Milli Qəhrəmanları &uuml;&ccedil;&uuml;n təsis edilmiş təqa&uuml;dlə bağlı xərclər daxildir.</p>'),
(69, 61, '20 yanvar şəhidlərinin ailələrinə verilən təqaüd', 272530, '1', '<p><strong>272530. &ldquo;20 yanvar şəhidlərinin ailələrinə verilən təqa&uuml;d&rdquo;&nbsp;</strong>maddəsinə 20 yanvar şəhidlərinin ailələri &uuml;&ccedil;&uuml;n təsis edilmiş təqa&uuml;dlə bağlı xərclər daxildir.</p>'),
(70, 61, 'Fərdi təqaüdlər', 272540, '1', '<p><strong>272540. &ldquo;Fərdi təqa&uuml;dlər&rdquo;&nbsp;</strong>maddəsinə qanunvericiliklə m&uuml;əyyən olunmuş şəxslərə təsis edilmiş təqa&uuml;dlə bağlı xərclər daxildir.</p>'),
(71, 61, 'Şəhid ailələrinə verilən təqaüdlər', 272550, '1', '<p><strong>272550. &ldquo;Şəhid ailələrinə verilən təqa&uuml;dlər&rdquo;&nbsp;</strong>maddəsinə şəhid ailələri &uuml;&ccedil;&uuml;n təsis edilmiş təqa&uuml;dlə bağlı xərclər daxildir.</p>'),
(72, 61, '20 yanvar  hadisələri ilə əlaqədar əlil olmuş şəxslərə verilən təqaüd', 272560, '1', '<p><strong>272560. &ldquo;20 yanvar hadisələri ilə əlaqədar əlil olmuş şəxslərə verilən təqa&uuml;d&rdquo;&nbsp;</strong>maddəsinə 1990-cı ilin 20 yanvar hadisələri ilə əlaqədar sağlamlıqlarını itirərək əlil olmuş şəxslərə təsis edilmiş təqa&uuml;d&uuml;n &ouml;dənilməsi ilə bağlı xərclər daxildir.</p>'),
(73, 61, 'Tələbələrə verilən təqaüdlər', 272570, '1', '<p><strong>272570. &ldquo;Tələbələrə verilən təqa&uuml;dlər&rdquo;&nbsp;</strong>maddəsinə m&ouml;vcud qanunvericiliyə əsasən doktorantlar, ali təhsil, orta ixtisas və ilk peşə-ixtisas təhsili m&uuml;əssisələrinin tələbələrinə və şagirdlərinə, həm&ccedil;inin Azərbaycan Milli Elmlər Akademiyasının magistratura səviyyəsi &uuml;zrə tələbələrə və bu profildə olan hərbi məktəblərdə təhsil alan tələbələrə &ouml;dənilən təqa&uuml;dlərlə bağlı xərclər daxildir.</p>'),
(74, 61, 'Digər təqaüdlər', 272590, '1', '<p><strong>272590. &ldquo;Digər təqa&uuml;dlər&rdquo;&nbsp;</strong>maddəsinə 272510-272570-ci maddələrində &ouml;z əksini tapmayan və mahiyyət etibarilə təqa&uuml;d hesab edilən&nbsp;digər xərclər daxildir.</p>'),
(75, 62, 'Məcburi köçkünlər və onlara bərabər tutulan şəxslərə ödənişlər', 272610, '1', '<p><strong>272610. &ldquo;Məcburi k&ouml;&ccedil;k&uuml;nlərə və onlara bərabər tutulan şəxslərə &ouml;dənişlər&rdquo;&nbsp;</strong>maddəsinə qanunvericiliklə m&uuml;əyyən olunmuş qaydada məcburi k&ouml;&ccedil;k&uuml;nlər və onlara bərabər tutulan şəxslərə təyin olunan m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(76, 62, 'Digər ödənişlər', 272690, '1', '<p><strong>272690. &ldquo;Digər &ouml;dənişlər&rdquo;&nbsp;</strong>maddəsinə qanunvericiliklə m&uuml;əyyən olunmuş qaydada məcburi k&ouml;&ccedil;k&uuml;nlərə və məskunlaşmamış qa&ccedil;qınlara verilən digər &ouml;dənişlər &uuml;zrə xərclər daxildir.</p>'),
(77, 63, 'Xidmət göstərənlərə ödənişlər', 272910, '1', '<p><strong>272910. &ldquo;Xidmət g&ouml;stərənlərə &ouml;dənişlər&rdquo;</strong>&nbsp;maddəsinə ahıllara xidmət g&ouml;stərən sosial iş&ccedil;ilərə qanunvericilikdə nəzərdə tutulan &ouml;dənişlər (nəqliyyat xərci, paltar, ayaqqabı və bu qəbildən olan digər &ouml;dənişlər) daxildir.</p>'),
(78, 63, 'Avtomobil növbəsində dayanan əlillərə kompensasiya', 272920, '1', '<p><strong>272920. &ldquo;Avtomobil n&ouml;vbəsində dayanan əlillərə kompensasiya&rdquo;&nbsp;</strong>maddəsinə qanunvericiliklə m&uuml;əyyən olunmuş qaydada pulsuz avtomobil almaq &uuml;&ccedil;&uuml;n n&ouml;vbəyə dayanmış əlillərə avtomobil verilmədikdə, onlara nəqliyyat xərci kimi &ouml;dənilən kompensasiya ilə bağlı xərclər daxildir.</p>'),
(79, 63, 'Xüsusi təhsilə cəlb edilənlərə müavinət', 272930, '1', '<p><strong>272930. &ldquo;X&uuml;susi təhsilə cəlb edilənlərə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə x&uuml;susi təhsilə cəlb olunanların valideynlərinə və ya digər qanuni n&uuml;mayəndələrinə uşaqlarını x&uuml;susi təhsil m&uuml;əssisələrinə, sağlamlıq və reabilitasiya m&uuml;əssisələrinə mərkəzlərinə və tibb m&uuml;əssisələrinə aparıb-gətirmək, evdə təhsilə cəlb olunanların valideynlərinə və ya digər qanuni n&uuml;mayəndələrinə uşaqlarını sağlamlıq və reabilitasiya mərkəzlərinə və tibb m&uuml;əssisələrinə aparıb-gətirmək &uuml;&ccedil;&uuml;n verilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(80, 63, 'İnsan alverindən zərər çəkmişlərə müavinət', 272940, '1', '<p><strong>272940. &ldquo;İnsan alverindən zərər &ccedil;əkmişlərə m&uuml;avinət&rdquo;</strong>&nbsp;maddəsinə insan alverindən zərər &ccedil;əkmiş şəxslərə reinteqrasiya d&ouml;vr&uuml;ndə &ouml;dənilən m&uuml;avinətlə bağlı xərclər daxildir.</p>'),
(81, 63, 'Digər ödənişlər', 272990, '1', '<p><strong>272990. &ldquo;Digər &ouml;dənişlər&rdquo;</strong>&nbsp;maddəsinə 272910-272940-cı maddələrdə nəzərdə tutulmayan və mahiyyət etibarilə sosial yardım &uuml;zrə &ouml;dəniş hesab edilən digər xərclər daxildir.</p>'),
(82, 68, 'Yaşayış binaları', 311110, '1', '<p><strong>311110. &ldquo;Yaşayış binaları&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n təyinatlardan olan yaşayış binalarının alınması və tikintisi ilə bağlı xərclər daxildir. Bu maddə &ldquo;311111. Yaşayış binalarının alınması&rdquo; və &ldquo;311112. Yaşayış binalarının tikintisi&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(83, 68, 'Qeyri-yaşayış binaları', 311120, '1', '<p><strong>311120. &ldquo;Qeyri-yaşayış binaları&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n təyinatlardan olan qeyri-yaşayış bina və tikililərin (inzibati, idarəetmə, hərbi təyinatlı və digər qeyri-yaşayış tikililərinin) alınması və tikintisi ilə bağlı xərclər daxildir. Bu maddə &ldquo;311121. Qeyri-yaşayış binalarının alınması&rdquo; və &ldquo;311122. Qeyri-yaşayış binalarının tikintisi&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(84, 68, 'Qurğular (tikintilər)', 311130, '1', '<p><strong>311130. &ldquo;Qurğular (tikintilər)&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n n&ouml;v qurğuların alınması və tikintisi ilə bağlı xərclər daxildir. Bu maddə &ldquo;311131. Qurğuların (tikintilərin) alınması&rdquo; və &ldquo;311122. Qurğuların (tikintilərin) tikintisi&rdquo; yarımmaddələrindən ibarətdir</p>'),
(85, 68, 'Ötürücü qurğular', 311140, '1', '<p><strong>311140. &ldquo;&Ouml;t&uuml;r&uuml;c&uuml; qurğular&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n n&ouml;v &ouml;t&uuml;r&uuml;c&uuml; qurğuların alınması, tikilməsi və quraşdırılması ilə bağlı xərclər daxildir. Bu maddə &ldquo;311141. &Ouml;t&uuml;r&uuml;c&uuml; qurğuların alınması&rdquo; və &ldquo;311142. &Ouml;t&uuml;r&uuml;c&uuml; qurğuların tikilməsi&rdquo; yarımmaddələrindən ibarətdir.</p>'),
(86, 68, 'Maşın və avadanlıqlar', 311150, '1', '<p><strong>311150. &ldquo;Maşın və avadanlıqlar&rdquo;&nbsp;</strong>maddəsinə g&uuml;c maşınları və avadanlıqları, o c&uuml;mlədən elektrik enerjisi və istilik hasil edən maşın-generatorlar və m&uuml;xtəlif n&ouml;v enerji (onlardan su enerjisi, k&uuml;lək enerjisi, istilik enerjisi, elektrik enerjisi, g&uuml;nəş enerjisi) hasil edən maşın-m&uuml;hərriklər və bu kimi başqa g&uuml;c maşınları və avadanlıqları, iş maşınları və avadanlıqları, poliqrafiya sahəsində, yol-tikinti, sualtı-texniki işlərdə və digər sahələrdə istifadə olunan başqa maşınlar və avadanlıqlar, &ouml;l&ccedil;&uuml; cihazları, o c&uuml;mlədən dozatorlar, ampermetrlər, barometrlər, vatmetrlər, su&ouml;l&ccedil;ənlər, vakuumetrlər, vaporimetrlər, voltmetrlər, h&uuml;nd&uuml;rl&uuml;k&ouml;l&ccedil;ənlər, qalvonometrlər, geodeziya cihazları, indikatorlar, kompaslar, manometrlər, xronometrlər, x&uuml;susi tərəzilər, kassa aparatları və başqa &ouml;l&ccedil;&uuml; cihazları, tənzimləyici cihazlar və qurğular, o c&uuml;mlədən oksigenli tənəff&uuml;s cihazları, elektrik, pnevmatik və hidravlik tənzimləyən cihazlar, avtomatik idarəetmə pultları, dispet&ccedil;er nəzarəti &uuml;&ccedil;&uuml;n xətti qurğular və bu kimi başqa tənzimləyici cihazlar və qurğular, laboratoriya avadanlıqları, o c&uuml;mlədən pirometrlər, nizamlayıcılar, kalorimetrlər, nəmliyi m&uuml;əyyən edən cihazlar, distillə qazanları, sorucu şkaflar, mikroskoplar və bu qəbildən olan başqa laboratoriya avadanlıqları, hesablama texnikası, o c&uuml;mlədən elektron hesablayıcılar, idarə&ccedil;i və analoji maşınlar (komp&uuml;terlər, printerlər, skanerlər, surət&ccedil;ıxaran maşınlar, fakslar, telefon aparatları), rəqəmli hesablayıcı maşınlar və qurğular, bu kimi başqa hesablama texnikası, tibbi avadanlıqlar, o c&uuml;mlədən diş həkimləri &uuml;&ccedil;&uuml;n kreslolar, cərrahiyyə stolları, x&uuml;susi avadanlıq bərkidilmiş &ccedil;arpayılar, x&uuml;susi dezinfeksiya avadanlıqları, qank&ouml;&ccedil;&uuml;rmə stansiyaları və bu kimi başqa tibbi avadanlıqlar, elektrik m&uuml;hərrikləri, dizel-qeneratorlar, metalkəsən dəzgahlar, k&ouml;rp&uuml;l&uuml; kranlar, &uuml;zən nasos stansiyaları, səyyar akkumulyatorlar, qaynaq aqreqatları, qazma və təmir aqreqatları, emalatxanalarda, kabinetlərdə və otaqlarda quraşdırılacaq avadanlıqlar, kinoaparatlar, işıqlandırma aparatları, səhnə avadanlıqları, musiqi alətləri, televizorlar, radio-aparatlar, x&uuml;susi elmi tədqiqat avadanlıqları, avtomatlaşdırılmış telefon stansiyalarının avadanlıqları, avtomobillərdə və ya digər nəqliyyat vasitələrində yerləşdirilən yanğıns&ouml;nd&uuml;rən maşınlar və avadanlıqlar, mexanikləşdirilmiş yanğın nəqliyyatları, yol hərəkətini tənzimləyən texniki qurğular və işıqforlar, paltaryuyan, paltartikən və qabyuyan maşınlar, soyuducular, kondisionerlər, tozsoranlar, dispenserlər, proyektorlar, idman avadanlıqları, bu kimi başqa maşın və avadanlıqlar,&nbsp;silah, d&ouml;y&uuml;ş sursatı, digər hərbi təyinatlı avadanlıq&nbsp;və sair maşın və avadanlıqların alınması və quraşdırılması ilə bağlı xərclər daxildir.</p>'),
(87, 69, 'Hava nəqliyyatı vasitələri', 311210, '1', '<p><strong>311210. &ldquo;Hava nəqliyyatı vasitələri&rdquo;&nbsp;</strong>maddəsinə hava nəqliyyatı vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(88, 69, 'Dəmiryolu nəqliyyatı vasitələri', 311220, '1', '<p><strong>311220. &ldquo;Dəmir yolu nəqliyyatı vasitələri&rdquo;&nbsp;</strong>maddəsinə dəmir yolu nəqliyyatı vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(89, 69, 'Su nəqliyyatı vasitələri', 311230, '1', '<p><strong>311230. &ldquo;Su nəqliyyatı vasitələri&rdquo;</strong>&nbsp;maddəsinə su nəqliyyatı vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(90, 69, 'Xidmət nəqliyyatı vasitələri', 311240, '1', '<p><strong>311240. &ldquo;Xidmət nəqliyyatı vasitələri&rdquo;</strong>&nbsp;maddəsinə trolleybuslar, tramvaylar, avtobuslar, refrejeratorlar və bu kimi digər xidmət nəqliyyatları vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(91, 69, 'İstehsalat nəqliyyatı vasitələri', 311250, '1', '<p><strong>311250. &ldquo;İstehsalat nəqliyyatı vasitələri&rdquo;</strong>&nbsp;maddəsinə ekskavatorlar, avtoqreyderlər, avtoskreperlər, qoşqulu skreperlər, traktorlar, buldozerlər, kombaynlar, elektrokarlar, motorollerlər, idman &uuml;&ccedil;&uuml;n nəzərdə tutulmayan motosikletlər və velosipedlər, mopedlər, araba nəqliyyatları və bu kimi digər istehsalat nəqliyyatları vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(92, 69, 'Avtomobil nəqliyyatı vasitələri', 311260, '1', '<p><strong>311260. &ldquo;Avtomobil nəqliyyatı vasitələri&rdquo;&nbsp;</strong>maddəsinə minik və y&uuml;k avtomobil nəqliyyatı vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(93, 69, 'İdman nəqliyyatı vasitələri', 311270, '1', '<p><strong>311270. &ldquo;İdman nəqliyyatı vasitələri&rdquo;</strong>&nbsp;maddəsinə idman avtomobilləri, idman motosikletləri, idman velosipedləri və bu kimi digər idman nəqliyyatları&nbsp;vasitələrinin alınması və istehsalı ilə bağlı xərclər daxildir.</p>'),
(94, 71, 'Yaşayış binalarının əsaslı təmiri', 312110, '1', '<p><strong>312110. &ldquo;Yaşayış binalarının əsaslı təmiri&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n təyinatlardan olan yaşayış binalarının əsaslı təmiri və əsaslı təmir işləri ilə bağlı layihə-smeta sənədlərinin hazırlanması &uuml;zrə xərclər daxildir.</p>'),
(95, 71, 'Qeyri-yaşayış binalarının əsaslı təmiri', 312120, '1', '<p><strong>312120. &ldquo;Qeyri-yaşayış binalarının əsaslı təmiri&rdquo;</strong>&nbsp;maddəsinə b&uuml;t&uuml;n təyinatlardan olan qeyri-yaşayış bina və tikililərin (inzibati, idarəetmə, hərbi təyinatlı və digər qeyri-yaşayış tikililərinin) əsaslı təmiri və əsaslı təmir işləri ilə bağlı layihə-smeta sənədlərinin hazırlanması &uuml;zrə xərclər daxildir.</p>'),
(96, 71, 'Qurğuların (tikintilərin) əsaslı təmiri', 312130, '1', '<p><strong>312130. &ldquo;Qurğuların (tikintilərin) əsaslı təmiri&rdquo;&nbsp;</strong>maddəsinə b&uuml;t&uuml;n n&ouml;v qurğuların əsaslı təmiri və əsaslı təmir işləri ilə bağlı layihə-smeta sənədlərinin hazırlanması &uuml;zrə xərclər daxildir.</p>'),
(97, 71, 'Ötürücü qurğuların əsaslı təmiri', 312140, '1', '<p><strong>312140. &ldquo;&Ouml;t&uuml;r&uuml;c&uuml; qurğuların əsaslı təmiri&rdquo;&nbsp;</strong>maddəsinə b&uuml;t&uuml;n n&ouml;v &ouml;t&uuml;r&uuml;c&uuml; qurğuların əsaslı təmiri və əsaslı təmir işləri ilə bağlı layihə-smeta sənədlərinin hazırlanması &uuml;zrə xərclər daxildir.</p>'),
(98, 71, 'Maşın və avadanlıqların əsaslı təmiri', 312150, '1', '<p><strong>312150. &ldquo;Maşın və avadanlıqların əsaslı təmiri&rdquo;&nbsp;</strong>maddəsinə g&uuml;c maşınları və avadanlıqları, o c&uuml;mlədən elektrik enerjisi və istilik hasil edən maşın-generatorlar və m&uuml;xtəlif n&ouml;v enerji (onlardan su enerjisi, k&uuml;lək enerjisi, istilik enerjisi, elektrik enerjisi, g&uuml;nəş enerjisi) hasil edən maşın-m&uuml;hərriklər və bu kimi başqa g&uuml;c maşınları və avadanlıqları, iş maşınları və avadanlıqları, poliqrafiya sahəsində, yol-tikinti, sualtı-texniki işlərdə və digər sahələrdə istifadə olunan başqa maşınlar və avadanlıqlar, &ouml;l&ccedil;&uuml; cihazları, o c&uuml;mlədən dozatorlar, ampermetrlər, barometrlər, vatmetrlər, su&ouml;l&ccedil;ənlər, vakuumetrlər, vaporimetrlər, voltmetrlər, h&uuml;nd&uuml;rl&uuml;k&ouml;l&ccedil;ənlər, qalvonometrlər, geodeziya cihazları, hiqroskoplar, indikatorlar, kompaslar, manometrlər, xronometrlər, x&uuml;susi tərəzilər, kassa aparatları və başqa &ouml;l&ccedil;&uuml; cihazları, tənzimləyici cihazlar və qurğular, o c&uuml;mlədən oksigenli tənəff&uuml;s cihazları, elektrik, pnevmatik və hidravlik tənzimləyən cihazlar, avtomatik idarəetmə pultları, dispet&ccedil;er nəzarəti &uuml;&ccedil;&uuml;n xətti qurğular və bu kimi digər tənzimləyici cihazlar və qurğular, laboratoriya avadanlıqları, o c&uuml;mlədən pirometrlər, nizamlayıcılar, kalorimetrlər, nəmliyi m&uuml;əyyən edən cihazlar, distillə qazanları, sorucu şkaflar, mikroskoplar və bu qəbildən olan başqa laboratoriya avadanlıqları, hesablama texnikası, o c&uuml;mlədən elektron hesablayıcılar, idarə&ccedil;i və analoji maşınlar (komp&uuml;terlər, printerlər, skanerlər, surət&ccedil;ıxaran maşınlar, fakslar, telefon aparatları), rəqəmli hesablayıcı maşınlar və qurğular, bu kimi digər hesablama texnikası, tibbi avadanlıqlar, o c&uuml;mlədən diş həkimləri &uuml;&ccedil;&uuml;n kreslolar, cərrahiyyə stolları, x&uuml;susi avadanlıq bərkidilmiş &ccedil;arpayılar, x&uuml;susi dezinfeksiya avadanlıqları, qank&ouml;&ccedil;&uuml;rmə stansiyaları və bu kimi digər tibbi avadanlıqlar, elektrik m&uuml;hərrikləri, dizel-qeneratorlar, metalkəsən dəzgahlar, k&ouml;rp&uuml;l&uuml; kranlar, &uuml;zən nasos stansiyaları, səyyar akkumulyatorlar, qaynaq aqreqatları, qazma və təmir aqreqatları, emalatxanalarda, kabinetlərdə və otaqlarda quraşdırılacaq avadanlıqlar, kinoaparatlar, işıqlandırma aparatları, səhnə avadanlıqları, musiqi alətləri, televizorlar, radio-aparatlar, x&uuml;susi elmi tədqiqat avadanlıqları, avtomatlaşdırılmış telefon stansiyalarının avadanlıqları, avtomobillərdə və ya digər nəqliyyat vasitələrində yerləşdirilən yanğıns&ouml;nd&uuml;rən maşınlar və avadanlıqlar, mexanikləşdirilmiş yanğın nəqliyyatları, paltaryuyan, paltartikən və qabyuyan maşınlar, soyuducular, kondisionerlər, tozsoranlar, dispenserlər, bu kimi digər maşın və avadanlıqlar,&nbsp;silah, d&ouml;y&uuml;ş sursatı, digər hərbi təyinatlı avadanlıq&nbsp;və sair maşın və avadanlıqların əsaslı təmiri və əsaslı təmir işləri ilə bağlı layihə-smeta sənədlərinin hazırlanması &uuml;zrə xərclər daxildir.</p>'),
